DROP TABLE IF EXISTS `school_profiles`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `school_profiles` (
  `id` int NOT NULL AUTO_INCREMENT,
  `code` varchar(50) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '學校代碼 (如 SPCC, QC, LSC)',
  `name` varchar(200) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '學校全名',
  `name_zh` varchar(200) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '學校中文名',
  `focus_areas` json NOT NULL COMMENT '面試重點領域數組',
  `interview_style` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '面試風格',
  `notes` text COLLATE utf8mb4_unicode_ci COMMENT '備注說明',
  `created_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP,
  `updated_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`),
  UNIQUE KEY `code` (`code`),
  KEY `idx_code` (`code`)
) ENGINE=InnoDB AUTO_INCREMENT=14 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='學校檔案';
