  `average_score` decimal(5,2) DEFAULT NULL COMMENT '平均分數',
  `strengths` json DEFAULT NULL COMMENT '優勢點數組',
  `weaknesses` json DEFAULT NULL COMMENT '薄弱點數組',
  `suggestions` text COLLATE utf8mb4_unicode_ci COMMENT '改進建議',
  `created_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`),
  UNIQUE KEY `session_id` (`session_id`),
  KEY `idx_session_id` (`session_id`),
  CONSTRAINT `session_summaries_ibfk_1` FOREIGN KEY (`session_id`) REFERENCES `sessions` (`id`) ON DELETE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='會話總結';
